import { render } from '@testing-library/react';
import { Carousel } from 'antd';
import { useSelector } from 'react-redux'
// import BannerReducer from '../../Redux/Reducer/BannerReducer'
import { rootReducer } from '../../Redux/Types/configStore'
type Props = {};


const Banner = (props: Props) => {

  const { arrImg } = useSelector((state: rootReducer) => state.BannerReducer)

  console.log('arrImg', arrImg);

  const contentStyle: React.CSSProperties = {
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background

  };

  const renderImg = () => {
    return arrImg.map((item, index) => {
      return  <div key={index}>
      <div style={{...contentStyle, backgroundImage:`url(${item.hinhAnh})`}}>
        <img src={item.hinhAnh} alt="banner" className="w-full" />
      </div>
    </div>
     
    })
  }

  return (
    <Carousel autoplay>
      {renderImg()}
    </Carousel>
  )
}
export default Banner;
